package Lec1;

public class Maximum_of_three_nos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a = 70;
     int b = 100;
     int c = 90;
     
     if (a>b && a>c)
     {
    	 System.out.println("a is greater");
     }
     if (b>a && b>c)
     {
    	 System.out.println("b is greater");
     }
     else
     {
    	 System.out.println("c is greater");
     }
	}

}
