package Lec1;

public class Sum_of_n_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int n = 10;
      int i = 1;
      int sum = 0;
      while(i<n)
      {
    	  sum = sum+i;
    	  System.out.println("sum of n numbers is "+sum);
    	  i = i+1;
    	
      }
	}

}
